mapper.select:
select msg.*,user.USER_NAME as fromName from t_message msg left join t_users user on msg.FROM_ID = user.USER_ID where msg.DELETED_CODE = 0 and msg.MEMBER_ID = #{message.memberId}
and msg.MESSAGE_READ = #{message.messageRead}
and msg.title like concat('%',#{message.title},'%')
and msg.SYS_MESSAGE like concat('%',#{message.sysMessage},'%')
order by msg.CREATE_AT desc
